CREATE TABLE GLINFO_USERS (USERID VARCHAR(20) PRIMARY KEY, FIRSTNAME VARCHAR(20) NOT NULL, LASTNAME VARCHAR(20) NOT NULL, USERNAME VARCHAR(100), PHONE VARCHAR(20), EMAIL VARCHAR(100), ADDRESS1 VARCHAR(50), ADDRESS2 VARCHAR(50), CITY VARCHAR(50), REGION VARCHAR(50), POSTAL VARCHAR(20), COUNTRY VARCHAR(50), FAXNUMBER VARCHAR(20), CARDNUMBER VARCHAR(50), EXPIRECARD VARCHAR(10),SECURITYCARD VARCHAR(3), NAMEONCARD VARCHAR(100), CARDADDRESS1 VARCHAR(50), CARDADDRESS2 VARCHAR(50), CARDZIP VARCHAR(50), CARDREGION VARCHAR(50), CARDCITY VARCHAR(50), CARDCOUNTRY VARCHAR(50), FIRSTPRODUCT VARCHAR(50), registrationDate VARCHAR(50), SSDUNS VARCHAR(50), SECONDPRODUCT VARCHAR(50), KEYTYPE VARCHAR(50), CELLPHONE VARCHAR(50), CORPORATENAME VARCHAR(50), FULLNAME VARCHAR(50));  
CREATE TABLE GLINFO_USERS_CARDS(USERID VARCHAR(20) PRIMARY KEY, APPID VARCHAR(50), CARDNAME VARCHAR(50), CARDTYPE VARCHAR(50), CARDNUMBER VARCHAR(50), CARDEXPIRATION VARCHAR(15), CARDSECURITY VARCHAR(15), APPDATE DATE); 
CREATE TABLE GLINFO_PRODUCTS (PRODUCTID INT IDENTITY PRIMARY KEY, PRODUCTNAME CHAR(50), PRODUCT_WOO_ID CHAR(50), PROUDCT_WOO_INSTANCE CHAR(100), PRODUCT_WOO_URL CHAR(250), VENDORID CHAR(25), ORACLEID CHAR(50), DYNAMICSID CHAR(50), DIVISION CHAR(30)); 
CREATE TABLE GLINFO_ITSM(TICKETID BIGINT IDENTITY PRIMARY KEY, CUSTID CHAR(100), ORACLEID CHAR(100), DYNAMICSID CHAR(100), SUMMARY CHAR(200), PRIORITY CHAR(10), RES1 CHAR(100), RES2 CHAR(100), RES3 CHAR(100), RES4 CHAR(100), TICKETSTATUS CHAR(100), OPENDATE DATE, CLOSEDATE DATE); 
CREATE TABLE GLINFO_ITSM_DETAIL(DETAILID BIGINT IDENTITY PRIMARY KEY, TICKETID BIGINT, LINENUMBER CHAR(50), LINEDETAIL CHAR(1000));
CREATE TABLE GLINFO_CHANGE(CHANGEID BIGINT PRIMARY KEY, WORKEFFORTSIZE CHAR(25), BUSUNIT CHAR(25), ENGINEERID CHAR(50), DESCRIPTION CHAR(3500), STATUS CHAR(10), APPROVALID CHAR(100), CHANGESTART DATE, CHANGEEND DATE, HOLDINFO CHAR(1000), COMMENTS CHAR(3000));

